import { ReactNode } from "react";
import { FrameShapeType, FunctionArgumentType } from "../types";
import { finderSize } from "../utils/isFinder";

interface FrameShapeWrapperType {
  row: number;
  col: number;
  cellSize: number;
  module: boolean[][];
  flipArray?: string[];
  children: ReactNode;
}

const FrameShapeWrapper = ({
  row,
  col,
  cellSize,
  flipArray,
  children,
}: FrameShapeWrapperType) => {
  const finderSizePx = cellSize * finderSize;
  const originalSize = 100;
  const scale = finderSizePx / originalSize;

  const x = col * cellSize;
  const y = row * cellSize;

  let tx = x;
  let ty = y;
  let sx = scale;
  let sy = scale;

  const flipH = flipArray?.includes("horizontal");
  const flipV = flipArray?.includes("vertical");

  if (flipH) {
    tx = x + finderSizePx;
    sx = -scale;
  }

  if (flipV) {
    ty = y + finderSizePx;
    sy = -scale;
  }

  return (
    <g transform={`translate(${tx}, ${ty}) scale(${sx}, ${sy})`}>{children}</g>
  );
};

